interface SearchBase {
  objectID: string,
  _id: string,
  publicDateMs: number,
}

type SearchLocation = {
  type: "Point",
  coordinates: number[],
}

interface SearchComment extends SearchBase {
  userId: string,
  baseScore: number,
  isDeleted: boolean,
  retracted: boolean,
  deleted: boolean,
  spam: boolean,
  legacy: boolean,
  userIP: string | null,
  createdAt: string,
  postedAt: string,
  af: boolean,
  authorDisplayName?: string | null,
  authorUserName?: string | null,
  authorSlug?: string | null,
  postId?: string,
  postTitle?: string | null,
  postSlug?: string,
  postIsEvent?: boolean,
  postGroupId?: string | null,
  tags: Array<string>, // an array of tag _ids that are associated with the comment, whether via tagId or via tagRels
  body: string,
  tagId?: string,
  tagName?: string,
  tagSlug?: string,
  tagCommentType?: import("../../lib/collections/comments/types").TagCommentType,
}

interface SearchSequence extends SearchBase {
  title: string | null,
  userId: string,
  createdAt: string,
  af: boolean,
  authorDisplayName?: string | null,
  authorUserName?: string | null,
  authorSlug?: string | null,
  plaintextDescription: string,
  bannerImageId?: string | null,
}

interface SearchUser extends SearchBase {
  username: string,
  displayName: string,
  createdAt: string,
  isAdmin: boolean,
  profileImageId?: string,
  bio: string,
  htmlBio: string,
  karma: number,
  slug: string,
  jobTitle?: string,
  organization?: string,
  careerStage?: string[],
  website: string,
  groups: Array<string>,
  af: boolean,
  socialMediaUrls: Partial<Record<SocialMediaSiteName | "website", string>> | null,
  _geoloc?: SearchLocation,
  mapLocationAddress?: string,
  tags: Array<string>,
  profileUpdatedAt: Date,
}

interface SearchPost extends SearchBase {
  url: string | null,
  title: string | null,
  slug: string,
  baseScore: number,
  status: number,
  curated: boolean,
  legacy: boolean,
  commentCount: number,
  userIP: string | null,
  createdAt: string,
  postedAt: string,
  isFuture: boolean,
  isEvent: boolean,
  viewCount: number,
  lastCommentedAt: string | null,
  draft: boolean,
  af: boolean,
  tags: Array<string>,
  authorSlug?: string | null,
  authorDisplayName?: string | null,
  authorFullName?: string | null,
  feedName?: string,
  feedLink?: string | null,
  body: string,
}

interface SearchTag extends SearchBase {
  name: string,
  slug: string,
  core: boolean,
  defaultOrder: number,
  suggestedAsFilter: boolean,
  postCount: number,
  wikiOnly: boolean,
  isSubforum: boolean,
  description: string,
  bannerImageId?: string | null,
  parentTagId?: string | null,
}

interface SearchLocalgroup extends SearchBase {
  name: string,
  nameInAnotherLanguage: string | null,
  organizerIds: Array<string>,
  lastActivity: string | null,
  types: Array<string>,
  categories: Array<string> | null,
  isOnline: boolean,
  _geoloc?: SearchLocation,
  location: string | null,
  contactInfo: string | null,
  facebookLink: string | null,
  facebookPageLink: string | null,
  meetupLink: string | null,
  slackLink: string | null,
  website: string | null,
  bannerImageId: string | null,
  inactive: boolean,
  deleted: boolean,
  description: string,
  createdAt: string,
}
